import React, { useRef, useEffect } from 'react';
import d3 from 'd3';
import styled from 'styled-components';
const ChartWrapper = styled.svg``;

const WorkoutRecords = () => {
	const svgRef = useRef();
	const width = 600;
	const height = 150;
	useEffect(() => {
		const svg = d3.select(svgRef.current);
		svg
			.attr('viewBox', [0, 0, width, height])
			.attr('font-family', 'sans-serif')
			.attr('font-size', 10);
		var x = d3.scaleBand().range([0, width]).domain(myGroups).padding(0.01);
		svg
			.append('g')
			.attr('transform', 'translate(0,' + height + ')')
			.call(d3.axisBottom(x));
		var myGroups = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J'];
		var myVars = ['v1', 'v2', 'v3', 'v4', 'v5', 'v6', 'v7', 'v8', 'v9', 'v10'];
		// Build X scales and axis:
		var y = d3.scaleBand().range([height, 0]).domain(myVars).padding(0.01);
		svg.append('g').call(d3.axisLeft(y));
	});
	return (
		<>
			<ChartWrapper ref={svgRef}></ChartWrapper>
		</>
	);
};

export default WorkoutRecords;
