import React, { useCallback, useEffect } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { Button, Row, Col } from 'antd';
import Router from 'next/router';
import SignupForm from './SignupForm';
const Header = styled.header`
	padding: 8px 0;
	background-color: #3498db;
	height: 56px;
`;
const HeaderContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	height: 20px;
`;
const GridRow = styled(Row)`
	height: 100%;
	align-items: center;
`;
const Logo = styled.div``;
const FlexMenu = styled.div`
	margin-left: auto;
`;
const MainWrapper = styled.div`
	background-image: url(https://github.githubassets.com/images/modules/site/heroes/simple-codelines.svg);
	background-color: #74b9ff;
`;

const MainContainer = styled.div`
	display: flex;
	align-items: center;
	padding: 64px 0;
	flex-wrap: wrap;
`;
const DescriptionWrapper = styled.div`
	padding: 0 24px;
`;
const DescriptionTitle = styled.div`
	line-height: 1;
	font-size: 64px;
	margin-bottom: 16px;
`;
const DescriptionContent = styled.div`
	line-height: 1.5;
	font-size: 20px;
	margin-bottom: 16px;
`;

const SigninLayout = () => {
	const onLogin = useCallback(() => {
		Router.push('/login');
	});
	const onSignin = useCallback(() => {
		Router.push('/');
	});
	const { descriptionTitle, descriptionContent } = useSelector(
		state => state.post,
	);
	const { me, registerDone } = useSelector(state => state.user);
	useEffect(() => {
		if (me && me.id) {
			Router.push('/main');
		}
	});
	useEffect(() => {
		if (registerDone && me && me.id) {
			Router.push('/main');
		}
	});
	return (
		<>
			<Header>
				<GridRow>
					<Col span={16} offset={4}>
						<HeaderContainer>
							<Logo>FITTIL</Logo>
							<FlexMenu>
								<Button type="text" onClick={onLogin}>
									로그인
								</Button>
								<Button type="text" onClick={onSignin}>
									회원가입
								</Button>
							</FlexMenu>
						</HeaderContainer>
					</Col>
				</GridRow>
			</Header>
			<main>
				<MainWrapper>
					<Row>
						<MainContainer>
							<Col
								xs={{ span: 24, offset: 0 }}
								sm={{ span: 9, offset: 3 }}
								md={{ span: 9, offset: 3 }}
								xl={{ span: 7, offset: 5 }}
							>
								<DescriptionWrapper>
									<DescriptionTitle>{descriptionTitle}</DescriptionTitle>
									<DescriptionContent>{descriptionContent}</DescriptionContent>
								</DescriptionWrapper>
							</Col>
							<Col
								xs={{ span: 16, offset: 4 }}
								sm={{ span: 9, offset: 0 }}
								md={{ span: 9, offset: 0 }}
								xl={{ span: 6, offset: 1 }}
							>
								<SignupForm></SignupForm>
							</Col>
						</MainContainer>
					</Row>
				</MainWrapper>
			</main>
			<footer></footer>
		</>
	);
};

export default SigninLayout;
