import React, { useCallback } from 'react';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import { Button, Row, Col } from 'antd';
import Router from 'next/router';
import useInput from '../../hooks/useInput';
import { REGISTER_USER_REQUEST } from '../../actions';
const Header = styled.header`
	padding: 8px 0;
	background-color: #3498db;
	height: 56px;
`;
const HeaderContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	height: 20px;
`;
const GridRow = styled(Row)`
	height: 100%;
	align-items: center;
`;
const Logo = styled.div``;
const FlexMenu = styled.div`
	margin-left: auto;
`;
const MainWrapper = styled.div`
	background-image: url(https://github.githubassets.com/images/modules/site/heroes/simple-codelines.svg);
	background-color: #74b9ff;
`;

const MainContainer = styled.div`
	display: flex;
	align-items: center;
	padding: 64px 0;
	flex-wrap: wrap;
`;
const DescriptionWrapper = styled.div`
	padding: 0 24px;
`;
const DescriptionTitle = styled.div`
	line-height: 1;
	font-size: 64px;
	margin-bottom: 16px;
`;
const DescriptionContent = styled.div`
	line-height: 1.5;
	font-size: 20px;
	margin-bottom: 16px;
`;

const LoginFormWrapper = styled.div`
	background-color: #fff;
	border-radius: 5px;
	padding: 15px;
`;
const InputWrapper = styled.div`
	margin-bottom: 15px;
`;
const InputLabel = styled.label`
	display: block;
`;
const Input = styled.input`
	width: 100%;
	border-radius: 5px;
`;
const SignupButton = styled(Button)`
	background-color: #34495e;
	color: white;
	margin-bottom: 15px;
`;
const ControlNote = styled.p`
	font-size: 12px;
`;
const SigninLayout = () => {
	const dispatch = useDispatch();
	const [nickname, onChangeNickname] = useInput('');
	const [email, onChangeEmail] = useInput('');
	const [password, onChangePassword] = useInput('');
	const handleSubmit = useCallback(e => {
		e.preventDefault();
		dispatch({
			type: REGISTER_USER_REQUEST,
			data: {
				nickname,
				email,
				password,
			},
		});
	});
	const onLogin = useCallback(() => {
		Router.push('/login');
	});
	const onSignin = useCallback(() => {
		Router.push('/');
	});
	return (
		<>
			<Header>
				<GridRow>
					<Col span={16} offset={4}>
						<HeaderContainer>
							<Logo>FITTIL</Logo>
							<FlexMenu>
								<Button type="text" onClick={onLogin}>
									로그인
								</Button>
								<Button type="text" onClick={onSignin}>
									회원가입
								</Button>
							</FlexMenu>
						</HeaderContainer>
					</Col>
				</GridRow>
			</Header>
			<main>
				<MainWrapper>
					<Row>
						<MainContainer>
							<Col
								xs={{ span: 24, offset: 0 }}
								sm={{ span: 9, offset: 3 }}
								md={{ span: 9, offset: 3 }}
								xl={{ span: 7, offset: 5 }}
							>
								<DescriptionWrapper>
									<DescriptionTitle>
										Built for
										<br /> 헬린이
									</DescriptionTitle>
									<DescriptionContent>
										GitHub is a development platform inspired by the way you
										work. From open source to business, you can host and review
										code, manage projects, and build software alongside 50
										million developers.
									</DescriptionContent>
								</DescriptionWrapper>
							</Col>
							<Col
								xs={{ span: 16, offset: 4 }}
								sm={{ span: 9, offset: 0 }}
								md={{ span: 9, offset: 0 }}
								xl={{ span: 6, offset: 1 }}
							>
								<LoginFormWrapper>
									<form onSubmit={handleSubmit}>
										<InputWrapper>
											<InputLabel>Username</InputLabel>
											<Input onChange={onChangeNickname}></Input>
										</InputWrapper>
										<InputWrapper>
											<InputLabel>Email</InputLabel>
											<Input type="email" onChange={onChangeEmail}></Input>
										</InputWrapper>
										<InputWrapper>
											<InputLabel>password</InputLabel>
											<Input
												type="password"
												onChange={onChangePassword}
											></Input>
										</InputWrapper>
										<ControlNote>
											Make sure it's at least 15 characters OR at least 8
											characters including a number and a lowercase letter.
											Learn more.
										</ControlNote>
										<SignupButton size="large" shape="round" block>
											Signup For FITTIL
										</SignupButton>
										<ControlNote>
											By clicking “Sign up for GitHub”, you agree to our Terms
											of Service and Privacy Statement. We’ll occasionally send
											you account related emails.
										</ControlNote>
									</form>
								</LoginFormWrapper>
							</Col>
						</MainContainer>
					</Row>
				</MainWrapper>
			</main>
			<footer></footer>
		</>
	);
};

export default SigninLayout;
