import React, { useState, useCallback } from 'react';
import { Menu, Dropdown, Modal } from 'antd';
import { EllipsisOutlined } from '@ant-design/icons';
import { useSelector, useDispatch } from 'react-redux';
import { DELETE_NOTE_REQUEST } from '../actions';
{
	/* <EllipsisOutlined />; */
}

const CardSetting = ({ note }) => {
	const dispatch = useDispatch();
	const [deleteModalVisible, setDeleteModalVisible] = useState(false);
	const [editModalVisible, setEditModalVisible] = useState(false);
	// const [deleteModalVisible, setDeleteModalVisible] = useState(false);
	const { me } = useSelector(state => state.user);
	const { mainNotes } = useSelector(state => state.note);
	const onEditNote = useCallback(() => {}, []);
	const onDeleteNote = useCallback(() => {
		setDeleteModalVisible(true);
	}, []);
	const onMakePrivate = useCallback(() => {}, []);
	const onMakePublic = useCallback(() => {}, []);

	const handleDeleteCancel = useCallback(() => {
		setDeleteModalVisible(false);
	}, []);
	const handleDeleteOk = useCallback(() => {
		setDeleteModalVisible(false);
		dispatch({
			type: DELETE_NOTE_REQUEST,
			data: {
				id: note.id,
			},
		});
	}, []);

	const myNoteMenu = (
		<Menu>
			<Menu.Item>
				<span onClick={onEditNote}>수정</span>
			</Menu.Item>
			<Menu.Item>
				<span onClick={onDeleteNote}>삭제</span>
			</Menu.Item>
			{note.public_availability === 'public' ? (
				<Menu.Item>
					<span onClick={onMakePrivate}>Private 전환</span>
				</Menu.Item>
			) : (
				<Menu.Item>
					<span onClick={onMakePublic}>Public 전환</span>
				</Menu.Item>
			)}
		</Menu>
	);
	const othersNoteMenu = (
		<Menu>
			<Menu.Item>
				<span onClick={onEditNote}>신고</span>
			</Menu.Item>
		</Menu>
	);

	return (
		<>
			{me && me.id === note.UserId ? (
				<>
					<Dropdown overlay={myNoteMenu}>
						<a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
							<EllipsisOutlined />
						</a>
					</Dropdown>
					{/* 삭제모달 */}
					<Modal
						title="게시물을 삭제하시겠어요?"
						visible={deleteModalVisible}
						onOk={handleDeleteOk}
						onCancel={handleDeleteCancel}
					>
						<p>이 게시물을 삭제하시겠어요?</p>
					</Modal>
				</>
			) : (
				<Dropdown overlay={othersNoteMenu}>
					<a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
						<EllipsisOutlined />
					</a>
				</Dropdown>
			)}
		</>
	);
};

export default CardSetting;
