import React, { useState, useCallback } from 'react';
import { Menu, Dropdown, Modal } from 'antd';
import { EllipsisOutlined } from '@ant-design/icons';
import { useSelector, useDispatch } from 'react-redux';
import { DELETE_NOTE_REQUEST } from '../actions';
import CreateNoteForm from './CreateNoteForm';

const CardSetting = ({ note }) => {
	const dispatch = useDispatch();
	// 삭제 모달
	const [deleteModalVisible, setDeleteModalVisible] = useState(false);
	// 수정 모달
	const [editModalVisible, setEditModalVisible] = useState(false);
	// 신고 모달
	const [reportModalVisible, setReportModalVisible] = useState(false);
	const { me } = useSelector(state => state.user);
	const { mainNotes } = useSelector(state => state.note);
	const onEditNote = useCallback(() => {
		setEditModalVisible(true);
	}, []);
	const onDeleteNote = useCallback(() => {
		setDeleteModalVisible(true);
	}, []);
	const onReportNote = useCallback(() => {});
	const onMakePrivate = useCallback(() => {}, []);
	const onMakePublic = useCallback(() => {}, []);
	const handleEditCancel = useCallback(() => {
		setEditModalVisible(false);
	}, []);
	const handleEditOk = useCallback(() => {
		setEditModalVisible(false);
	}, []);
	const handleDeleteCancel = useCallback(() => {
		setDeleteModalVisible(false);
	}, []);
	const handleDeleteOk = useCallback(() => {
		setDeleteModalVisible(false);
		dispatch({
			type: DELETE_NOTE_REQUEST,
			data: {
				id: note.id,
			},
		});
	}, []);

	const myNoteMenu = (
		<Menu>
			<Menu.Item>
				<span onClick={onEditNote}>수정</span>
			</Menu.Item>
			<Menu.Item>
				<span onClick={onDeleteNote}>삭제</span>
			</Menu.Item>
			{note.public_availability === 'public' ? (
				<Menu.Item>
					<span onClick={onMakePrivate}>Private 전환</span>
				</Menu.Item>
			) : (
				<Menu.Item>
					<span onClick={onMakePublic}>Public 전환</span>
				</Menu.Item>
			)}
		</Menu>
	);
	const othersNoteMenu = (
		<Menu>
			<Menu.Item>
				<span onClick={onReportNote}>신고</span>
			</Menu.Item>
		</Menu>
	);

	return (
		<>
			{me && me.id === note.UserId ? (
				<>
					<Dropdown overlay={myNoteMenu}>
						<a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
							<EllipsisOutlined />
						</a>
					</Dropdown>
					{/* 삭제모달 */}
					<Modal
						title="게시물을 삭제하시겠어요?"
						visible={deleteModalVisible}
						onOk={handleDeleteOk}
						onCancel={handleDeleteCancel}
					>
						<p>이 게시물을 삭제하시겠어요?</p>
					</Modal>
					{/* 수정 모달 */}
					<Modal
						title="게시글 수정"
						visible={editModalVisible}
						onOk={handleEditOk}
						// confirmLoading={confirmLoading}
						onCancel={handleEditCancel}
					>
						{/* <p>{ModalText}</p> */}
						<FormWrapper>
							<form onSubmit={handleSubmit}>
								<InputWrapper>
									<InputLabel>Title</InputLabel>
									<InputTitle
										value={title}
										onChange={onChangeTitle}
										// placeholder="간단한 제목을 적어주세요"
									></InputTitle>
								</InputWrapper>
								<InputWrapper>
									<InputLabel>Content</InputLabel>
									<InputDescription
										value={content}
										onChange={onChangeContent}
										rows="10"
										// placeholder="오늘 어떤 운동하셨나요?"
									></InputDescription>
								</InputWrapper>
								{/* 운동 강도 선택 */}
								<InputWrapper>
									<Select
										showSearch
										style={{ width: '100%' }}
										placeholder="운동 강도"
										optionFilterProp="children"
										// onChange={onChange}
										// onFocus={onFocus}
										// onBlur={onBlur}
										// onSearch={onSearch}
										onChange={onChangeSelectedLevel}
										filterOption={(input, option) =>
											option.children
												.toLowerCase()
												.indexOf(input.toLowerCase()) >= 0
										}
									>
										<Select.Option value="강">
											<Badge color="blue" text="강" />
										</Select.Option>
										<Select.Option value="중">
											<Badge color="yellow" text="중" />
										</Select.Option>
										<Select.Option value="약">
											<Badge color="red" text="약" />
										</Select.Option>
									</Select>
								</InputWrapper>
								{/* 운동 부위 선택 */}
								<InputWrapper>
									<Select
										style={{ width: '100%' }}
										placeholder="운동부위를 입력해주세요"
										mode="multiple"
										onChange={onChangeSelectedPartItem}
										dropdownRender={menu => (
											<div>
												{menu}
												<Divider style={{ margin: '4px 0' }} />
												<div
													style={{
														display: 'flex',
														flexWrap: 'nowrap',
														padding: 8,
													}}
												>
													<Input
														style={{ flex: 'auto' }}
														value={partItem}
														onChange={onChangePartItem}
													/>
													<a
														style={{
															flex: 'none',
															padding: '8px',
															display: 'block',
															cursor: 'pointer',
														}}
														onClick={addItem}
													>
														<PlusOutlined /> 부위 추가
													</a>
												</div>
											</div>
										)}
									>
										{partItems.map(partItem => (
											<Select.Option key={partItem}>{partItem}</Select.Option>
										))}
									</Select>
								</InputWrapper>
								<Divider />
								<FormCheckBox>
									<InputLabel htmlFor="public">
										<input
											type="radio"
											name="public"
											checked={selectedOption === 'public'}
											value="public"
											onChange={onChangeSelectedOption}
										/>
										&emsp;&emsp; Public
									</InputLabel>
									<UnlockOutlined
										style={{
											fontSize: '25px',
											position: 'absolute',
											top: 0,
											left: '15px',
										}}
									/>
									<span style={{ fontSize: '14px' }}>
										&emsp;&emsp;&emsp;&nbsp;&nbsp;누구든 회원님의 일지를 볼 수
										있습니다.
									</span>
								</FormCheckBox>
								<FormCheckBox>
									<InputLabel htmlFor="private">
										<input
											type="radio"
											name="privacy"
											value="private"
											checked={selectedOption === 'private'}
											onChange={onChangeSelectedOption}
										/>
										&emsp;&emsp; Private
									</InputLabel>
									<LockOutlined
										style={{
											fontSize: '25px',
											position: 'absolute',
											top: 0,
											left: '15px',
										}}
									/>
									<span style={{ fontSize: '14px' }}>
										&emsp;&emsp;&emsp;&nbsp;&nbsp;회원님의 일지를 비공개합니다.
									</span>
								</FormCheckBox>
								<Divider></Divider>
								<MakeNoteButton htmlType="submit" block>
									일지 작성하기
								</MakeNoteButton>
							</form>
						</FormWrapper>
					</Modal>
				</>
			) : (
				<Dropdown overlay={othersNoteMenu}>
					<a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
						<EllipsisOutlined />
					</a>
				</Dropdown>
			)}
		</>
	);
};

export default CardSetting;
