import React, { useCallback } from 'react';
import { Row, Col, Select, Badge, Input, Divider } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import AppHeader from '../component/layout/AppHeader';
import styled from 'styled-components';
import useInput from '../hooks/useInput';
const Subhead = styled.div`
	border-bottom: 1px solid gray;
	padding-bottom: 15px;
	margin-bottom: 20px;
`;
const SubheadHeading = styled.h1``;
const SubheadDescription = styled.div``;
const FormWrapper = styled.div`
	border: 1px solid gray;
	border-radius: 5px;
	padding: 20px;
`;
const InputWrapper = styled.div`
	margin-bottom: 15px;
	/* width: 80%; */
	text-align: center;
`;
const InputLabel = styled.label``;
const InputTitle = styled.input`
	border-radius: 5px;
	width: 90%;
`;
const InputDescription = styled.textarea`
	border-radius: 5px;
	width: 90%;
`;
const New = () => {
	const [partItems, setPartItems, onChangePartItems] = useInput([
		'가슴',
		'등',
		'어깨',
		'하체',
	]);
	const [part, setPart, onChangePart] = useInput('');
	const addItem = useCallback(() => {
		setPartItems(prev => [...prev, part]);
	}, [part]);

	return (
		<>
			<AppHeader></AppHeader>
			<main>
				<Row>
					<Col
						xs={{ span: 24, offset: 0 }}
						sm={{ span: 24, offset: 0 }}
						md={{ span: 12, offset: 6 }}
						xl={{ span: 12, offset: 6 }}
					>
						<Subhead>
							<SubheadHeading>Create a new Note</SubheadHeading>
							<SubheadDescription>
								노트에는 그날의 운동, 식단 등의 기록을 할 수 있습니다.
							</SubheadDescription>
						</Subhead>
						<FormWrapper>
							<form>
								<InputWrapper>
									<InputLabel></InputLabel>
									<InputTitle placeholder="간단한 제목을 적어주세요"></InputTitle>
								</InputWrapper>
								<InputWrapper>
									<InputLabel></InputLabel>
									<InputDescription
										rows="10"
										placeholder="오늘 어떤 운동하셨나요?"
									></InputDescription>
								</InputWrapper>
								{/* 운동 강도 선택 */}
								<InputWrapper>
									<Select
										showSearch
										style={{ width: '90%' }}
										placeholder="운동 강도"
										optionFilterProp="children"
										// onChange={onChange}
										// onFocus={onFocus}
										// onBlur={onBlur}
										// onSearch={onSearch}
										filterOption={(input, option) =>
											option.children
												.toLowerCase()
												.indexOf(input.toLowerCase()) >= 0
										}
									>
										<Select.Option value="강">
											<Badge color="blue" text="강" />
										</Select.Option>
										<Select.Option value="중">
											<Badge color="yellow" text="중" />
										</Select.Option>
										<Select.Option value="약">
											<Badge color="red" text="약" />
										</Select.Option>
									</Select>
								</InputWrapper>
								{/* 운동 부위 선택 */}
								<InputWrapper>
									<Select
										style={{ width: '90%' }}
										placeholder="운동부위를 입력해주세요"
										dropdownRender={menu => (
											<div>
												{menu}
												<Divider style={{ margin: '4px 0' }} />
												<div
													style={{
														display: 'flex',
														flexWrap: 'nowrap',
														padding: 8,
													}}
												>
													<Input
														style={{ flex: 'auto' }}
														value="?"
														onChange={onChangePart}
													/>
													<a
														style={{
															flex: 'none',
															padding: '8px',
															display: 'block',
															cursor: 'pointer',
														}}
														onClick={addItem}
													>
														<PlusOutlined /> 부위 추가
													</a>
												</div>
											</div>
										)}
									>
										{partItems.map(partItem => (
											<Select.Option key={partItem}>{partItem}</Select.Option>
										))}
									</Select>
								</InputWrapper>
							</form>
						</FormWrapper>
					</Col>
				</Row>
			</main>
		</>
	);
};

export default New;
