import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import { Avatar, Button } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import useInput from '../../hooks/useInput';
import DetailEditForm from './DetailEditForm';
import UserDetails from './UserDetails';
const ProfileWrapper = styled.div`
	/* margin-top: 30px; */
	padding: 0px 25px;
`;
const AvatarWrapper = styled.div`
	position: relative;
	width: 50%;
	&:after {
		display: block;
		padding-bottom: 100%;
	}
`;
const CustomAvatar = styled(Avatar)`
	/* min-height: 100px;
	min-width: 100px;
	max-height: 230px;
	max-width: 230px; */
	width: 100%;
	height: 100%;
`;

const UserProfile = styled.div``;
const UserName = styled.div`
	padding: 20px 0;
	color: #7f8c8d;
	font-size: 17px;
`;
const UserNote = styled.div``;
const EditButton = styled(Button)`
	margin-top: 15px;
	border-radius: 5px;
`;
// const EditForm = styled.form``;
// const UserDetail = styled.div``;
const Profile = () => {
	const [isEditOpened, setIsEditOpend] = useState(false);
	const onEditProfile = useCallback(e => {
		e.preventDefault();
		setIsEditOpend(!isEditOpened);
	});
	return (
		<ProfileWrapper>
			<UserProfile>
				<AvatarWrapper>
					<CustomAvatar></CustomAvatar>
				</AvatarWrapper>

				<UserName>pius712</UserName>
			</UserProfile>
			<UserNote>React, Vue, Nodejs</UserNote>
			<EditButton size="small" block onClick={onEditProfile}>
				Edit Profile
			</EditButton>
			{isEditOpened ? (
				<DetailEditForm></DetailEditForm>
			) : (
				<UserDetails></UserDetails>
			)}
		</ProfileWrapper>
	);
};

export default Profile;
