import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import {
	IdcardOutlined,
	CompassOutlined,
	FacebookOutlined,
	InstagramOutlined,
} from '@ant-design/icons';
const ProfileItem = styled.li`
	display: flex;
	align-items: center;
`;
const UserDetails = () => {
	const dispatch = useDispatch();
	const { me } = useSelector(state => state.user);
	// console.log(me);
	// const note = me.note ? me.note : '';
	const job = me.job ? me.job : '';
	const location = me.location ? me.location : '';
	const facebook = me.facebook ? me.facebook : '';
	const instagram = me.instagram ? me.instagram : '';

	return (
		<>
			<ul>
				{me.job ? (
					<ProfileItem>
						<IdcardOutlined />
						{me.job}
					</ProfileItem>
				) : null}
				{me.location ? (
					<ProfileItem>
						<CompassOutlined />
						{me.location}
					</ProfileItem>
				) : null}
				{me.facebook ? (
					<ProfileItem>
						<FacebookOutlined />
						{me.facebook}
					</ProfileItem>
				) : null}
				{me.instagram ? <ProfileItem>{me.instagram}</ProfileItem> : null}
			</ul>
		</>
	);
};

export default UserDetails;
