import React from 'react';
import { Row, Col, Select } from 'antd';
import AppHeader from '../component/layout/AppHeader';
import styled from 'styled-components';
const Subhead = styled.div`
	border-bottom: 1px solid gray;
	padding-bottom: 15px;
	margin-bottom: 20px;
`;
const SubheadHeading = styled.h1``;
const SubheadDescription = styled.div``;
const FormWrapper = styled.div`
	border: 1px solid gray;
	border-radius: 5px;
	padding: 20px;
`;
const InputWrapper = styled.div`
	margin-bottom: 15px;
	/* width: 80%; */
	text-align: center;
`;
const InputLabel = styled.label``;
const InputTitle = styled.input`
	border-radius: 5px;
	width: 80%;
`;
const InputDescription = styled.textarea`
	border-radius: 5px;
	width: 80%;
`;
const New = () => {
	return (
		<>
			<AppHeader></AppHeader>
			<main>
				<Row>
					<Col
						xs={{ span: 24, offset: 0 }}
						sm={{ span: 24, offset: 0 }}
						md={{ span: 12, offset: 6 }}
						xl={{ span: 12, offset: 6 }}
					>
						<Subhead>
							<SubheadHeading>Create a new Note</SubheadHeading>
							<SubheadDescription>
								노트에는 그날의 운동, 식단 등의 기록을 할 수 있습니다.
							</SubheadDescription>
						</Subhead>
						<FormWrapper>
							<form>
								<InputWrapper>
									<InputLabel></InputLabel>
									<InputTitle placeholder="간단한 제목을 적어주세요"></InputTitle>
								</InputWrapper>
								<InputWrapper>
									<InputLabel></InputLabel>
									<InputDescription
										rows="10"
										placeholder="오늘 어떤 운동하셨나요?"
									></InputDescription>
								</InputWrapper>
								<Select
									showSearch
									style={{ width: 200 }}
									placeholder="운동 강도"
									optionFilterProp="children"
									// onChange={onChange}
									// onFocus={onFocus}
									// onBlur={onBlur}
									// onSearch={onSearch}
									filterOption={(input, option) =>
										option.children
											.toLowerCase()
											.indexOf(input.toLowerCase()) >= 0
									}
								>
									<Select.Option value="jack">Jack</Select.Option>
									<Select.Option value="lucy">Lucy</Select.Option>
									<Select.Option value="tom">Tom</Select.Option>
								</Select>
								,
							</form>
						</FormWrapper>
					</Col>
				</Row>
			</main>
		</>
	);
};

export default New;
