import React, { useState } from 'react';
import { Bubble } from 'react-chartjs-2';
const WorkoutRecords = () => {
	const [data, setData] = useState({
		xLabels: ['일', '월', '화', '수', '목', '금', '토'],
		yLabels: ['1', '2', '3', '4', '5'],
		datasets: [
			{
				data: ['월', '화', '금'],
				// data: {
				// 	x: 0,
				// 	y: 1,
				// 	r: 5,
				// },
				borderColor: '#2ecc71',
				backgroundColor: '#2ecc71',
			},
		],
	});
	const [options, setOptions] = useState({
		title: {
			display: true,
			text: '이달의 운동',
		},
		scales: {
			xAxes: [
				{
					type: 'category',
					display: true,
					position: 'top',
					scaleLabel: {
						display: true,
						labelString: 'Month',
					},
				},
			],
			yAxes: [
				{
					type: 'category',
					display: true,
					scaleLabel: {
						display: true,
						labelString: 'Request State',
					},
					ticks: {
						reverse: false,
					},
				},
			],
		},
	});
	return (
		<>
			<Bubble data={data} options={options}></Bubble>
		</>
	);
};

export default WorkoutRecords;
