import React, { useCallback } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { Button, Row, Col, Menu, Dropdown, Input } from 'antd';
import {
	NotificationOutlined,
	DownOutlined,
	PlusOutlined,
} from '@ant-design/icons';
import Router from 'next/router';
import Link from 'next/link';

const Header = styled.header`
	padding: 8px 0;
	background-color: #3498db;
	height: 53px;
`;

const HeaderItems = styled.div``;
const HeaderContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	height: 20px;
`;
const GridRow = styled(Row)`
	height: 100%;
	align-items: center;
`;
const Logo = styled.a`
	color: #2c3e50;
	font-size: 20px;
	padding: 10px;
`;
const FlexMenu = styled.div`
	margin-left: auto;
`;
const HeaderItem = styled.div`
	display: inline-block;
	margin-right: 16px;
	height: 100%;
`;
const DropDownLink = styled.a`
	color: black;
`;

const { Search } = Input;
const AppHeader = () => {
	const onLogin = useCallback(() => {
		// Router.push('/login');
	});
	const onSignin = useCallback(() => {
		// Router.push('/');
	});
	const menu = (
		<Menu>
			<Menu.Item>
				<a
					target="_blank"
					rel="noopener noreferrer"
					href="http://www.alipay.com/"
				>
					1st menu item
				</a>
			</Menu.Item>
			<Menu.Item>
				<a
					target="_blank"
					rel="noopener noreferrer"
					href="http://www.taobao.com/"
				>
					2nd menu item
				</a>
			</Menu.Item>
			<Menu.Item>
				<a
					target="_blank"
					rel="noopener noreferrer"
					href="http://www.tmall.com/"
				>
					3rd menu item
				</a>
			</Menu.Item>
			<Menu.Item danger>a danger item</Menu.Item>
		</Menu>
	);
	return (
		<Header>
			<GridRow>
				<Col
					// xs={{ span: 24, offset: 0 }}
					// sm={{ span: 20, offset: 2 }}
					md={{ span: 4, offset: 0 }}
					xl={{ span: 4, offset: 0 }}
				>
					<HeaderItem>
						<Logo>FIT-TIL</Logo>
					</HeaderItem>
				</Col>

				<Col md={{ span: 6, offset: 0 }} xl={{ span: 6, offset: 0 }}>
					<HeaderItems>
						<HeaderItem>운동일지</HeaderItem>
						<HeaderItem></HeaderItem>
						<HeaderItem></HeaderItem>
						<HeaderItem></HeaderItem>
					</HeaderItems>
				</Col>
				{/* 10  21*/}
				<Col md={{ span: 4, offset: 10 }} xl={{ span: 4, offset: 10 }}>
					<HeaderItem>
						<NotificationOutlined />
					</HeaderItem>
					<HeaderItem>
						<Dropdown overlay={menu} placement="bottomRight">
							<DropDownLink
								className="ant-dropdown-link"
								onClick={e => e.preventDefault()}
							>
								<PlusOutlined /> <DownOutlined />
							</DropDownLink>
						</Dropdown>
					</HeaderItem>
				</Col>
				{/* <HeaderContainer>
					<Link href="main">
						<Logo>FITTIL</Logo>
					</Link>
					<FlexMenu>
						<Button type="text" onClick={onLogin}>
							로그인
						</Button>
						<Button type="text" onClick={onSignin}>
							회원가입
						</Button>
					</FlexMenu>
				</HeaderContainer> */}
			</GridRow>
		</Header>
	);
};

export default AppHeader;
