import React, { useCallback } from 'react';
import styled from 'styled-components';
import { Button, Row, Col, Divider, Layout } from 'antd';
import Router from 'next/router';

const Header = styled.header`
	padding: 8px 0;
	background-color: #3498db;
	height: 56px;
`;
const HeaderContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	height: 20px;
`;
const GridRow = styled(Row)`
	height: 100%;
	align-items: center;
`;
const Logo = styled.div``;
const FlexMenu = styled.div`
	margin-left: auto;
`;
const MainWrapper = styled.div`
	background-image: url(https://github.githubassets.com/images/modules/site/heroes/simple-codelines.svg);
	background-color: #74b9ff;
`;

const MainContainer = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 64px 0;
`;
const DescriptionWrapper = styled.div``;
const DescriptionTitle = styled.div``;
const DescriptionContent = styled.div``;
const LoginFormWrapper = styled.div`
	background-color: #fff;
	border-radius: 5px;
	padding: 15px;
`;
const InputWrapper = styled.div`
	margin-bottom: 15px;
`;
const SignupButton = styled(Button)`
	background-color: #34495e;
	color: white;
`;
const SigninLayout = () => {
	const onLogin = useCallback(() => {
		Router.push('/login');
	});
	const onSignin = useCallback(() => {
		Router.push('/');
	});
	return (
		<>
			<Header>
				<GridRow>
					<Col span={16} offset={4}>
						<HeaderContainer>
							<Logo>FITTIL</Logo>
							<FlexMenu>
								<Button type="text" onClick={onLogin}>
									로그인
								</Button>
								<Button type="text" onClick={onSignin}>
									회원가입
								</Button>
							</FlexMenu>
						</HeaderContainer>
					</Col>
				</GridRow>
			</Header>
			<main>
				<MainWrapper>
					<Row>
						<Col span={12} offset={6}>
							<MainContainer>
								<DescriptionWrapper>
									<DescriptionTitle>
										Built for
										<br /> 헬린이
									</DescriptionTitle>
									<DescriptionContent>
										GitHub is a development platform inspired by the way you
										work. From open source to business, you can host and review
										code, manage projects, and build software alongside 50
										million developers.
									</DescriptionContent>
								</DescriptionWrapper>
								<LoginFormWrapper>
									<form>
										<InputWrapper>
											<label>Username</label>
											<input></input>
										</InputWrapper>
										<InputWrapper>
											<label>Email</label>
											<input></input>
										</InputWrapper>
										<InputWrapper>
											<label>password</label>
											<input></input>
										</InputWrapper>
										<div>
											Make sure it's at least 15 characters OR at least 8
											characters including a number and a lowercase letter.
											Learn more.
										</div>
										<SignupButton shape="round" block>
											Signup For FITTIL
										</SignupButton>
										<div>
											By clicking “Sign up for GitHub”, you agree to our Terms
											of Service and Privacy Statement. We’ll occasionally send
											you account related emails.
										</div>
									</form>
								</LoginFormWrapper>
							</MainContainer>
						</Col>
					</Row>
				</MainWrapper>
			</main>
			<footer></footer>
		</>
	);
};

export default SigninLayout;
