import React from 'react';
import NoteCard from './NoteCard';
import { useSelector } from 'react-redux';
import { Row, Col } from 'antd';
import styled from 'styled-components';

const MainPostsWrapper = styled.div`
	margin-bottom: 30px;
`;
const MainPostsTitle = styled.div`
	margin-bottom: 5px;
	font-size: 12px;
`;
const MainPosts = () => {
	const { mainNotes } = useSelector(state => state.post);
	return (
		<MainPostsWrapper>
			<MainPostsTitle>운동 요약</MainPostsTitle>
			<Row
				gutter={[
					{ xs: 8, sm: 16, md: 24, lg: 32 },
					{ xs: 4, sm: 8, md: 12, lg: 16 },
				]}
			>
				{mainNotes.map((note, index) => (
					<Col span="12">
						<NoteCard key={note.id} note={note}></NoteCard>
					</Col>
				))}
			</Row>
		</MainPostsWrapper>
	);
};

export default MainPosts;
