import React, { useCallback } from 'react';
import { Row, Col, Select, Badge, Input, Divider, Button, Layout } from 'antd';
import { PlusOutlined, LockOutlined, UnlockOutlined } from '@ant-design/icons';
import AppHeader from '../component/layout/AppHeader';
import styled from 'styled-components';
import useInput from '../hooks/useInput';
import wrapper from '../store/configureStore';
import axios from 'axios';
import { LOAD_MY_INFO_REQUEST } from '../actions';
import { END } from 'redux-saga';

const Subhead = styled.div`
	border-bottom: 1px solid gray;
	padding-bottom: 15px;
	margin-bottom: 20px;
`;
const SubheadHeading = styled.h1``;
const SubheadDescription = styled.div``;
const FormWrapper = styled.div`
	/* border: 1px solid gray; */
	border-radius: 5px;
	padding: 0 5%;
	margin-bottom: 20px;
`;
const InputWrapper = styled.div`
	margin-bottom: 15px;
	width: 100%;
	/* text-align: center; */
`;
const InputLabel = styled.label`
	display: block;
	text-align: left;
	/* margin-bottom: 5px; */
`;
const InputTitle = styled.input`
	border-radius: 5px;
	width: 100%;
`;
const InputDescription = styled.textarea`
	border-radius: 5px;
	width: 100%;
`;
const FormCheckBox = styled.div`
	position: relative;
	margin-top: 10px;
`;
const MakeNoteButton = styled(Button)`
	background-color: #3498db;
	color: white;
	margin-bottom: 15px;
`;
const New = () => {
	const [partItems, onChangePartItems, setPartItems] = useInput([
		'가슴',
		'등',
		'어깨',
		'하체',
	]);
	const [partItem, onChangePartItem, setPartItem] = useInput('');
	const [
		selectedPartItem,
		onChangeSelectedPartItem,
		setSelectedPartItem,
	] = useInput('');
	const [selectedLevel, onChangeSelectedLevel, setSelectedLevel] = useInput('');

	const addItem = useCallback(() => {
		setPartItems(prev => [...prev, partItem]);
	}, [partItem]);
	return (
		<>
			<AppHeader></AppHeader>
			<main>
				<Row>
					<Col
						xs={{ span: 24, offset: 0 }}
						sm={{ span: 24, offset: 0 }}
						md={{ span: 12, offset: 6 }}
						xl={{ span: 12, offset: 6 }}
					>
						<Subhead>
							<SubheadHeading>Create a new Note</SubheadHeading>
							<SubheadDescription>
								노트에는 그날의 운동, 식단 등의 기록을 할 수 있습니다.
							</SubheadDescription>
						</Subhead>
						<FormWrapper>
							<form>
								<InputWrapper>
									<InputLabel>Title</InputLabel>
									<InputTitle
									// placeholder="간단한 제목을 적어주세요"
									></InputTitle>
								</InputWrapper>
								<InputWrapper>
									<InputLabel>Content</InputLabel>
									<InputDescription
										rows="10"
										// placeholder="오늘 어떤 운동하셨나요?"
									></InputDescription>
								</InputWrapper>
								{/* 운동 강도 선택 */}
								<InputWrapper>
									<Select
										showSearch
										style={{ width: '100%' }}
										placeholder="운동 강도"
										optionFilterProp="children"
										// onChange={onChange}
										// onFocus={onFocus}
										// onBlur={onBlur}
										// onSearch={onSearch}
										onChange={onChangeSelectedLevel}
										filterOption={(input, option) =>
											option.children
												.toLowerCase()
												.indexOf(input.toLowerCase()) >= 0
										}
									>
										<Select.Option value="강">
											<Badge color="blue" text="강" />
										</Select.Option>
										<Select.Option value="중">
											<Badge color="yellow" text="중" />
										</Select.Option>
										<Select.Option value="약">
											<Badge color="red" text="약" />
										</Select.Option>
									</Select>
								</InputWrapper>
								{/* 운동 부위 선택 */}
								<InputWrapper>
									<Select
										style={{ width: '100%' }}
										placeholder="운동부위를 입력해주세요"
										mode="multiple"
										onChange={onChangeSelectedPartItem}
										dropdownRender={menu => (
											<div>
												{menu}
												<Divider style={{ margin: '4px 0' }} />
												<div
													style={{
														display: 'flex',
														flexWrap: 'nowrap',
														padding: 8,
													}}
												>
													<Input
														style={{ flex: 'auto' }}
														value={partItem}
														onChange={onChangePartItem}
													/>
													<a
														style={{
															flex: 'none',
															padding: '8px',
															display: 'block',
															cursor: 'pointer',
														}}
														onClick={addItem}
													>
														<PlusOutlined /> 부위 추가
													</a>
												</div>
											</div>
										)}
									>
										{partItems.map(partItem => (
											<Select.Option key={partItem}>{partItem}</Select.Option>
										))}
									</Select>
								</InputWrapper>
								<Divider />
								<FormCheckBox>
									<InputLabel htmlFor="public">
										<input type="radio" name="privacy" value="public" />
										&emsp;&emsp; Public
									</InputLabel>
									<UnlockOutlined
										style={{
											fontSize: '25px',
											position: 'absolute',
											top: 0,
											left: '15px',
										}}
									/>
									<span style={{ fontSize: '14px' }}>
										&emsp;&emsp;&emsp;&nbsp;&nbsp;누구든 회원님의 일지를 볼 수
										있습니다.
									</span>
								</FormCheckBox>
								<FormCheckBox>
									<InputLabel htmlFor="private">
										<input type="radio" name="privacy" value="private" />
										&emsp;&emsp; Private
									</InputLabel>
									<LockOutlined
										style={{
											fontSize: '25px',
											position: 'absolute',
											top: 0,
											left: '15px',
										}}
									/>
									<span style={{ fontSize: '14px' }}>
										&emsp;&emsp;&emsp;&nbsp;&nbsp;회원님의 일지를 비공개합니다.
									</span>
								</FormCheckBox>
								<Divider></Divider>
								<MakeNoteButton type="submit" block>
									일지 작성하기
								</MakeNoteButton>
							</form>
						</FormWrapper>
					</Col>
				</Row>
			</main>
			<Layout.Footer
				style={{
					// position: 'fixed',
					// bottom: 0,
					// width: '100%',
					textAlign: 'center',
				}}
			>
				FITTIL @2020 Created By Pius
			</Layout.Footer>
		</>
	);
};

export default New;
export const getServerSideProps = wrapper.getServerSideProps(
	async ({ store, req }) => {
		const cookie = req ? req.headers.cookie : '';
		axios.defaults.headers.Cookie = '';
		if (req && cookie) {
			axios.defaults.headers.Cookie = req.headers.cookie;
		}
		store.dispatch({
			type: LOAD_MY_INFO_REQUEST,
		});

		store.dispatch(END);
		await store.sagaTask.toPromise();
	},
);
